import { Link } from "react-router-dom";
import { useState } from "react";

//npx json-server --watch data/db.json --port 7000
const TaskList = ({ tasks }) => {

    const[filter, setFilter] = useState('All');

    return ( 
        <div className="task-list">
         <h2 style={{color: 'black'}}>What Needs To Be Done Today?</h2>
            
                <label>Filter by: </label>
                <select
                    value = {filter}
                    onChange={(e) => setFilter(e.target.value)}
                >
                    <option value="All">All Tasks</option>
                    <option value="PriorityLow">Priority - Low</option>
                    <option value="PriorityMed">Priority - Med</option>
                    <option value="PriorityHigh">Priority - High</option>
                    <option value="Catagory">Catagory</option>
                </select>
          {filter === 'All' ? (
              tasks.map((task) => (
                <div className="task-listing" key={task.id}>
                    <Link to={`/tasks/${task.id}`}>
                    <h2>{task.title}</h2>
                    <p>Due on: {task.date}</p>
                    <p>Subject: {task.subject}</p>
                    <p>Priority: {task.priority}</p>
                    </Link>
                </div>                   
            ))
          ) : (
            (filter) ===  'PriorityLow' ? (
              tasks.map((task) => (
                <div>
                  {task.priority === 'Low' ?( 
                    <div className="task-listing" key={task.id}>
                      <Link to={`/tasks/${task.id}`}>
                      <h2>{task.title}</h2>
                      <p>Due on: {task.date}</p>
                      <p>Subject: {task.subject}</p>
                      <p>Priority: {task.priority}</p>
                      </Link>
                    </div>
                  ) : (
                    console.log('None')
                  )}
                </div>                   
            ))               
            ) : (
              (filter) ===  'PriorityMed' ? (
                tasks.map((task) => (
                  <div>
                    {task.priority === 'Medium' ?( 
                      <div className="task-listing" key={task.id}>
                        <Link to={`/tasks/${task.id}`}>
                        <h2>{task.title}</h2>
                        <p>Due on: {task.date}</p>
                        <p>Subject: {task.subject}</p>
                        <p>Priority: {task.priority}</p>
                        </Link>
                      </div>
                    ) : (
                      console.log('None')
                    )}
                  </div>                   
              ))               
              ) : (
                (filter) ===  'PriorityHigh' ? (
                  tasks.map((task) => (
                    <div>
                      {task.priority === 'High' ?( 
                        <div className="task-listing" key={task.id}>
                          <Link to={`/tasks/${task.id}`}>
                          <h2>{task.title}</h2>
                          <p>Due on: {task.date}</p>
                          <p>Subject: {task.subject}</p>
                          <p>Priority: {task.priority}</p>
                          </Link>
                        </div>
                      ) : (
                        console.log('None')
                      )}
                    </div>                   
                ))               
                ) : (
                  <h2>Test</h2>
                )
              )
            )

          )}
          
        </div>
     );
}
 
export default TaskList;
