import { useState, useEffect } from "react";
import { useHistory } from 'react-router-dom'
import axios from "axios";

const Edit = () => {
    const{id} = useParams();
    const[title, setTitle] = useState('');
    const[date, setDate] = useState('');
    const[subject, setSubject] = useState('Other');
    const[priority, setPriority] = useState('Low');
    const[isLoading, setIsLoading] = useState(false);
    const history = useHistory();
    
    const getTaskInfo = () =>{
        const url = "http://localhost:7000/tasks/"+id;
        axios.get(url).then(response=>{
            setTitle(response.data.title);
            setDate(response.data.date);
            setPriority(response.data.priority);
            setSubject(response.data.subject);
        })
    }

    useEffect(()=>{
        getTaskInfo();
    },[true])

    
    return (
        <div className="create">
        <h2>Edit This Task</h2>
        <form onSubmit={handleSubmit}>
          <label>Edit Task Title:</label>
          <input
            type = "text"
            required
            value = {title}
            onChange={(e) => setTitle(e.target.value)}
          />
          <label>Edit Task Due Date (MM/DD/YYYY):</label>
          <input
            type = "text"
            required
            value = {date}
            onChange={(e) => setDate(e.target.value)}
          />
          <label>Edit Task Subject:</label>
          <select
            required
            value = {subject}
            onChange={(e) => setSubject(e.target.value)}
          >
            <option value="Math">Math</option>
            <option value="Science">Science</option>
            <option value="Social Studies">Social Studies</option>
            <option value="English">English</option>
            <option value="World Language">World Language</option>
            <option value="Chores">Chores</option>
            <option value="Hobbies">Hobbies</option>
            <option value="Other">Other</option>
          </select>
          <label>Edit Task Priority</label>
          <select
            required
            value = {priority}
            onChange={(e) => setPriority(e.target.value)}
            >
              <option value="Low">Low</option>
              <option value="Medium">Medium</option>
              <option value="High">High</option>
            </select>
          
          {!isLoading && <button>Edit Task</button>}
          {isLoading && <button disabled>Editing Task...</button>}
        </form>
      </div>
    );

    }
export default Edit;
