import { useState } from "react";
import TaskList from "./TaskList";

const Home = () => {

  const[tasks, setTasks] = useState([
    { title: 'Math homework', date: '10/31/21', description: 'Problems 1-10', id: 1 },
    { title: 'Reading', date: '10/18/21', description: 'Pages 10-20', id: 2 },
    { title: 'Walk dog', date: '10/05/21', description: 'Walk to and from park', id: 3 }
  ])

  const handleDelete = (id) => {
    const updatedTasks = tasks.filter(tasks => tasks.id !== id)
    setTasks(updatedTasks) 
  }

    return (
      <div className="home">
        <TaskList tasks={tasks={tasks}, handleDelete={handleDelete}} />
      </div>
    );
  }
   
  export default Home;
