import { useParams } from "react-router";
import { useHistory } from "react-router";
import useFetch from "./useFetch";
import { Link } from 'react-router-dom';

const TaskDetails = () => {
  const { id } = useParams();
  const { data: task, error, isLoading } = useFetch('http://localhost:7000/tasks/' + id);
  const history = useHistory();
  const url = "http://localhost:3000/edit/"+id;

  const handleDelete = () => {
    fetch('http://localhost:7000/tasks/' + task.id, {
      method: 'DELETE'
    }).then(() => {
      history.push('/');
    }) 
  }


  return (
    <div className="task-details">
      { isLoading && <div>Loading...</div> }
      { error && <div>{ error }</div> }
      
      { task && (
        <article>
          <h2>{ task.title }</h2>
          <p>Due by: { task.date }</p>
          <div>{ task.subject}</div>
          <div>{ task.priority}</div>
          <button onClick={handleDelete}>Complete Task</button>
          <Link to={url}><button>Edit Task</button></Link>
        </article>
      )}
    </div>
  );
}
 
export default TaskDetails;
