const TaskList = (props, title) => {
    const tasks = props.tasks;
    const header = props.title;
    console.log(tasks);

    return ( 
        <div className="task-list">
            <h2>{ header }</h2>
            {tasks.map((task) => (
                <div className="task-listing" key={task.id}>
                    <h2>{task.title}</h2>
                    <p>Due on: {task.date}</p>
                    <p>{task.description}</p>
                </div>                   
            ))}
        </div>
     );
}
 
export default TaskList;
