import useFetch from "./useFetch";
import { useParams } from "react-router";

const TaskList = ({ tasks }) => {
    const { id } = useParams();
    const { data: task, error, isLoading } = useFetch('http://localhost:7000/tasks/' + id);
    
    const handleClick =() => {
        fetch('http://localhost:7000/tasks' + task.id, {
            method: "DELETE"
        })
    }

    return ( 
        <div className="task-list">
         <h2 style={{color: 'black'}}>What Needs To Be Done Today?</h2>
            {tasks.map((task) => (
                <div className="task-listing" key={task.id}>
                    <h2>{task.title}</h2>
                    <p>Due on: {task.date}</p>
                    <p>{task.description}</p>
                    <button onClick={handleClick}>Complete Task</button>
                </div>                   
            ))}
        </div>
     );
}
 
export default TaskList;
